#AdventOfCode Day 6 Problems https://adventofcode.com/2022/day/6

inputString = ''

marker = 0
counter = 0
#Problem 1
while marker < len(inputString):
    sequence = inputString[marker:marker+4]
    for char in sequence:
        if sequence.count(char) > 1:
            marker = marker + 1
            break
        else:
            counter += 1
    if counter == 4:
        print('packet sequence = ',sequence)
        print('characters to be processed before start-of-packet = ', marker+4)
        break
    else:
        counter = 0
#Problem 2
while marker < len(inputString):
    sequence = inputString[marker:marker+14]
    for char in sequence:
        if sequence.count(char) > 1:
            marker = marker + 1
            break
        else:
            counter += 1
    if counter == 14:
        print('message = ',sequence)
        print('start-of-message =', marker+14)
        break
    else:
        counter = 0